import React,{ useState } from "react";
import { usePlaylist } from "../../Contexter/playListContext";
import "../../App.css";

export const PlaylistModal = () => {
  const [text, setText] = useState("");
  const {
    playList,
    playlistId,
    inputPlaylistBox,
    showPlaylistModal,
    dispatchplaylist,
  } = usePlaylist();

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleOnclick();
    }
  };

  const handleOnclick = () => {
    dispatchplaylist({ type: "DISPLAY_INPUT_BOX" });
    text !== "" && dispatchplaylist({ type: "CREATE_PLAY_LIST" });
    setText("");
  };

  const handlePlaylistCheckbox = (e, playlistId) => {
    let listId = e.target.id;
    if (e.target.checked === true) {
      let videoData = showPlaylistModal.videoData;
      dispatchplaylist({type: "SAVE_TO_PLAYLIST"});
    } else {
      let videoId = showPlaylistModal.videoData._id;
      dispatchplaylist({type: "REMOVE_FROM_PLAYLIST"});
    }
  };

  const itemChecked = (id) => {
    return playList
      .filter((item) => item._id === id)[0]
      .list.some((item) => {
        return item._id === showPlaylistModal.videoData._id ? true : false;
      });
  };

    return (
        <div className={`modal ${showPlaylistModal.status === false ? "modal-hide" : "modal-show"}`}>
            <div className="modal--window">
                <h1 className="modal--title">Playlist</h1>
                <span
                onClick={() => dispatchplaylist({ type: "SHOW_PLAYLIST_MODAL" })}>
                    <ion-icon name="close-outline" class="modal--icon"></ion-icon>
                </span>
                <div className="modal--content">
                {inputPlaylistBox && (
                    <div className="modal--input">
                        <input
                            type="text"
                            placeholder="Enter Playlist Name"
                            className="modal--input-box"
                            onChange={(event) => setText(event.target.value)}
                            onKeyPress={(event) => handleKeyPress(event)}
                        />
                        <button
                            className="btn btn-outline-secondary"
                            onClick={handleOnclick}>
                            Create
                        </button>
                    </div>
                )}
                {playList.map((item) => {
                    return (
                        <div key={item._id} className="playlist-names">
                            <input
                            type="checkbox"
                            name="playlist-item"
                            className="playlist-checkbox"
                            id={item._id}
                            checked={itemChecked(item._id)}
                            onChange={(e) => handlePlaylistCheckbox(e, playlistId)}
                            />
                            <label htmlFor={item._id}>{item.name}</label>
                        </div>
                    );
                })}
                </div>

                <div className="modal--buttons">
                    <button className="btn btn-secondary"
                        onClick={() => dispatchplaylist({ type: "DISPLAY_INPUT_BOX" })}>
                        New
                    </button>
                </div>
            </div>
        </div>
    );
};
